import { Component } from '@angular/core';
import { WeatherService } from '../../Services/weather.service';
import { CropService } from '../../Services/crop.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html'
})
export class WeatherComponent {
  lat: number = 0;
  lon: number = 0;
  predictedCrop: string | null = null;
  weatherData: any = null;

  soilData = {
    ph: 6.5,
    N: 0.3,
    P: 35.0,
    K: 50.0
  };

  constructor(
    private weatherService: WeatherService,
    private cropService: CropService
  ) {}

  fetchWeather() {
    this.weatherService.getWeather(this.lat, this.lon).subscribe({
      next: (data) => this.weatherData = data,
      error: (err) => console.error('Weather error:', err)
    });
  }

  onSubmit() {
    if (!this.weatherData) {
      alert("Please fetch weather data first.");
      return;
    }

    const input = {
      ...this.soilData,
      ph: this.soilData.ph,
      humidity: this.weatherData.humidity,
      temperature: this.weatherData.temperature,
      rainfall: this.weatherData.rainfall
    };

    this.cropService.predictCrop(input).subscribe({
      next: (res) => this.predictedCrop = res.recommended_crop,
      error: (err) => console.error('Prediction error:', err)
    });
  }
}
<div>
  <label>Latitude: <input [(ngModel)]="lat" type="number" /></label>
  <label>Longitude: <input [(ngModel)]="lon" type="number" /></label>
  <button (click)="fetchWeather()">Get Weather</button>
</div>

<div *ngIf="weatherData">
  <h3>Weather</h3>
  <p>Temperature: {{ weatherData.temperature }} °C</p>
  <p>Humidity: {{ weatherData.humidity }} %</p>
  <p>Rainfall: {{ weatherData.rainfall }} mm</p>
</div>

<hr />

<h3>Enter Soil Data</h3>
<div>
  <label>pH: <input [(ngModel)]="soilData.ph" type="number" step="0.1" /></label><br>
  <label>Nitrogen (%): <input [(ngModel)]="soilData.nitrogen" type="number" step="0.01" /></label><br>
  <label>Phosphorus (mg/kg): <input [(ngModel)]="soilData.phosphorus" type="number" /></label><br>
  <label>Potassium (mg/kg): <input [(ngModel)]="soilData.potassium" type="number" /></label><br>
</div>

<button (click)="onSubmit()">Submit</button>
