import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/Services/auth.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  standalone: false,
  styleUrls: ['./signup.component.scss'],
})
export class SignupComponent {
  userData: any = {
    firstName: '',
    lastName: '',
    username: '',
    password: '',
    imageUrl: ''   // ðŸ”¥ Add this field
  };
  form: FormGroup;

  constructor(private authService: AuthService, private router: Router, private fb: FormBuilder) {
    this.form = this.fb.group({
      name: ['', [Validators.required]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  onFileSelected(event: any): void {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.userData.imageUrl = e.target.result; // FULL Data URL
      };
      reader.readAsDataURL(file); // âš¡ï¸ NOT readAsText!
    }
  }


  onSignup(): void {
    console.log('Payload being sent:', this.userData);
    this.authService.signup(this.userData).subscribe({
      next: () => this.router.navigate(['/login']),
      error: (err) => console.error('Signup error:', err)
    });
  }

  onSubmit(): void {
    if (this.form.valid) {
      const { name, email, password } = this.form.value;
      this.userData.username = name;
      this.userData.email = email;
      this.userData.password = password;
      this.onSignup();
    } else {
      console.error('Form is invalid');
    }
  }
}
<div class="auth-wrapper d-flex align-items-center justify-content-center">
  <div class="card p-4" style="width: 350px;">
    <h4 class="mb-3 text-center">Sign Up</h4>
    <form [formGroup]="form" (ngSubmit)="onSubmit()">
      <div class="mb-3">
        <label class="form-label">Name</label>
        <input type="text" class="form-control" formControlName="name">
        <div *ngIf="form.controls['name'].invalid && form.controls['name'].touched"
             class="text-danger small">
          Name is required.
        </div>
      </div>

      <div class="mb-3">
        <label class="form-label">Email</label>
        <input type="email" class="form-control" formControlName="email">
        <div *ngIf="form.controls['email'].invalid && form.controls['email'].touched"
             class="text-danger small">
          A valid email is required.
        </div>
      </div>

      <div class="mb-3">
        <label class="form-label">Password</label>
        <input type="password" class="form-control" formControlName="password">
        <div *ngIf="form.controls['password'].invalid && form.controls['password'].touched"
             class="text-danger small">
          Password must be at least 6 characters.
        </div>
      </div>

      <div class="mb-3">
        <label class="form-label">Profile Picture</label>
        <input type="file" class="form-control" (change)="onFileSelected($event)">
      </div>

      <button class="btn btn-success w-100" [disabled]="form.invalid">
        Create Account
      </button>

      <p class="mt-2 text-center">
        Already have an account?
        <a routerLink="/auth/login">Login</a>
      </p>
    </form>
  </div>
</div>
