import { Component } from '@angular/core';
import { Features } from 'src/app/models/features/features.model';
import { FeatureService } from 'src/app/Services/feature.service';


@Component({
  selector: 'app-feature',
  templateUrl: './feature.component.html',
  styleUrls: ['./feature.component.scss']
})
export class FeatureComponent {
  lat!: number;
  lon!: number;
  loading = false;
  error: string | null = null;
  features: Features | null = null;

  constructor(private svc: FeatureService) {}

// feature.component.ts
fetchFeatures() {
  this.loading = true;
  this.featureService.getFeatures(this.lat, this.lon)
    .subscribe({
      next: f => { this.features = f; this.loading = false; },
      error: e => { this.error = e.message; this.loading = false; }
    });
}


  objectKeys = Object.keys;

  getFeatureValue(key: string): any {
    return this.features ? this.features[key as keyof Features] : null;
  }
}
<div class="p-4 max-w-md mx-auto">
  <h2 class="text-xl font-bold mb-4">Soil & Climate Features</h2>
  <form (ngSubmit)="fetchFeatures()" #f="ngForm" class="space-y-3">
    <div>
      <label>Latitude:</label>
      <input
        type="number"
        name="lat"
        [(ngModel)]="lat"
        required
        step="any"
        class="border p-1 w-full"
      />
    </div>
    <div>
      <label>Longitude:</label>
      <input
        type="number"
        name="lon"
        [(ngModel)]="lon"
        required
        step="any"
        class="border p-1 w-full"
      />
    </div>
    <button
      type="submit"
      [disabled]="f.invalid || loading"
      class="bg-blue-600 text-white px-4 py-2 rounded"
    >
      {{ loading ? 'Loading…' : 'Fetch Features' }}
    </button>
  </form>

  <div *ngIf="error" class="mt-4 text-red-600">{{ error }}</div>

  <div *ngIf="features" class="mt-6 space-y-2 bg-gray-100 p-4 rounded">
    <h3 class="font-semibold">Results:</h3>
    <div *ngFor="let key of objectKeys(features)">
      <strong>{{ key }}:</strong> {{ getFeatureValue(key) }}
    </div>
  </div>
</div>
