import { Component, AfterViewInit } from '@angular/core';
import * as L from 'leaflet';
import { WeatherService } from '../../Services/weather.service';
import { CropService } from '../../Services/crop.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.scss'],
})
export class WeatherComponent implements AfterViewInit {
  lat: number = 36.8;
  lon: number = 10.2;
  predictedCrop: string | null = null;
  weatherData: any = null;
  marker: L.Marker | null = null;
  loading: boolean = false; // Added loading property
  error: string | null = null; // Added error property

  soilData = {
    ph: 6.5,
    N: 0.3,
    P: 35,
    K: 50
  };

  constructor(
    private weatherService: WeatherService,
    private cropService: CropService
  ) {}

  ngAfterViewInit(): void {
    const map = L.map('map').setView([this.lat, this.lon], 6);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '&copy; OpenStreetMap contributors'
    }).addTo(map);

    map.on('click', (e: L.LeafletMouseEvent) => {
      this.lat = e.latlng.lat;
      this.lon = e.latlng.lng;

      if (this.marker) this.marker.remove();
      this.marker = L.marker([this.lat, this.lon]).addTo(map);

      this.fetchWeather(); // auto-fetch weather after selection
    });
  }

  fetchWeather() {
    this.loading = true; // Set loading to true when fetching weather
    this.weatherService.getWeather(this.lat, this.lon).subscribe({
      next: (data) => {
        this.weatherData = data;
        this.loading = false; // Set loading to false on successful data fetch
      },
      error: (err) => {
        console.error('Weather error:', err);
        this.error = 'Failed to load weather data.'; // Set error message
        this.loading = false; // Set loading to false on error
      }
    });
  }

  onSubmit() {
    if (!this.weatherData) {
      alert("Please click on the map to select a location and fetch weather.");
      return;
    }

    const input = {
      K: this.soilData.K,
      N: this.soilData.N,
      P: this.soilData.P,
      ph: this.soilData.ph,
      humidity: this.weatherData.humidity,
      temperature: this.weatherData.temperature,
      rainfall: this.weatherData.rainfall
    };

    this.cropService.predictCrop(input).subscribe({
      next: (res) => this.predictedCrop = res.recommended_crop,
      error: (err) => console.error('Prediction error:', err)
    });
  }
}
<h1 class="text-center mt-4">Crop Recommendation</h1> <!-- Added page title -->

<div class="row mt-4">
  <!-- Left column: Map and Submit -->
  <div class="col-md-6">
    <div id="map" class="border rounded" style="height: 400px;"></div>

    <div class="text-center mt-3">
      <button
        class="btn btn-success btn-lg"
        (click)="onSubmit()"
        [disabled]="!weatherData || loading">
        {{ loading ? 'Predicting...' : 'Recommend Crop' }}
      </button>
    </div>

    <div class="text-center mt-2">
      <button class="btn btn-info btn-sm" (click)="fetchWeather()">üå§ Fetch Weather</button>
    </div>

    <div class="text-center text-muted mt-2">
      üìç Selected: {{ lat.toFixed(4) }}, {{ lon.toFixed(4) }}
    </div>
  </div>

  <!-- Right column: Inputs + Results -->
  <div class="col-md-6">
    <div class="card shadow-sm mb-3">
      <div class="card-body">
        <h5 class="card-title">Soil Inputs</h5>
        <div class="mb-2">
          <label>pH</label>
          <input type="number" step="0.1" [(ngModel)]="soilData.ph" class="form-control" />
        </div>
        <div class="mb-2">
          <label>Nitrogen (N)</label>
          <input type="number" step="0.01" [(ngModel)]="soilData.N" class="form-control" />
        </div>
        <div class="mb-2">
          <label>Phosphorus (P)</label>
          <input type="number" step="1" [(ngModel)]="soilData.P" class="form-control" />
        </div>
        <div class="mb-2">
          <label>Potassium (K)</label>
          <input type="number" step="1" [(ngModel)]="soilData.K" class="form-control" />
        </div>
      </div>
    </div>

    <div *ngIf="weatherData" class="card shadow-sm mb-3">
      <div class="card-body">
        <h5 class="card-title">Weather</h5>
        <p>üå°Ô∏è Temperature: <strong>{{ weatherData.temperature }} ¬∞C</strong></p>
        <p>üíß Humidity: <strong>{{ weatherData.humidity }} %</strong></p>
        <p>üåßÔ∏è Rainfall: <strong>{{ weatherData.rainfall }} mm</strong></p>
      </div>
    </div>

    <div *ngIf="predictedCrop" class="alert alert-success text-center">
      üå± <strong>Recommended Crop:</strong> {{ predictedCrop }}
    </div>

    <div *ngIf="error" class="alert alert-danger text-center">
      {{ error }}
    </div>
  </div>
</div>
