import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  standalone: false,
  styleUrls: ['./signup.component.scss'],
})
export class SignupComponent {
  userData: any = {
    firstName: '',
    lastName: '',
    username: '',
    password: '',
    imageUrl: ''   // 🔥 Add this field
  };

  constructor(private authService: AuthService, private router: Router) {}

  onFileSelected(event: any): void {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.userData.imageUrl = e.target.result; // FULL Data URL
      };
      reader.readAsDataURL(file); // ⚡️ NOT readAsText!
    }
  }


  onSignup(): void {
    console.log('Payload being sent:', this.userData);
    this.authService.signup(this.userData).subscribe({
      next: () => this.router.navigate(['/login']),
      error: (err) => console.error('Signup error:', err)
    });
  }
}
<div class="auth-wrapper d-flex align-items-center justify-content-center">
    <div class="card p-4" style="width: 350px;">
        <div class="text-center mb-3">
            <img src="assets/images/logos/farmwiselogo.png" alt="Farmwise Logo" style="width: 150px;">
        </div>
        <h4 class="mb-3 text-center">Sign Up</h4>
        <form (ngSubmit)="onSignup()">
            <div class="mb-3">
                <label class="form-label">First Name</label>
                <input type="text" class="form-control" [(ngModel)]="userData.firstName" name="firstName" required>
            </div>

            <div class="mb-3">
                <label class="form-label">Last Name</label>
                <input type="text" class="form-control" [(ngModel)]="userData.lastName" name="lastName" required>
            </div>

            <div class="mb-3">
                <label class="form-label">Username</label>
                <input type="text" class="form-control" [(ngModel)]="userData.username" name="username" required>
            </div>

            <div class="mb-3">
                <label class="form-label">Password</label>
                <input type="password" class="form-control" [(ngModel)]="userData.password" name="password" required>
            </div>

            <div class="mb-3">
                <label class="form-label">Profile Picture</label>
                <input type="file" class="form-control" (change)="onFileSelected($event)" accept="image/*">
            </div>

            <button class="btn btn-success w-100" type="submit">Create Account</button>

            <p class="mt-2 text-center">
                Already have an account?
                <a routerLink="/auth/login">Login</a>
            </p>
        </form>
    </div>
</div>
