<div class="classifier-map">
  <div id="map"></div>
</div>

<div class="classifier-form text-center my-3">
  <button
    class="btn btn-success btn-lg"
    (click)="onSubmit()"
    [disabled]="loading || lat==null || lon==null">
    {{ loading ? 'Classifying...' : 'Classify Land' }}
  </button>
</div>

<div *ngIf="tileUrl" class="text-center mb-3">
  <img
    [src]="tileUrl"
    alt="Sentinel tile"
    class="img-fluid border rounded"
    style="max-width: 100%; height: 100%;"> <!-- Made the tile image responsive and larger -->
</div>

<div *ngIf="prediction" class="alert alert-success text-center">
  <strong>Prediction:</strong> {{ prediction }}
</div>

<div *ngIf="error" class="alert alert-danger text-center">
  {{ error }}
</div>
