// src/app/predict-land/predict-land.component.ts
import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { PredictionService } from '../../services/prediction.service';
import { CurrencyService }   from '../../services/currency.service';

@Component({
  selector: 'app-predict-land',
  templateUrl: './predict-land.component.html',
  styleUrls: ['./predict-land.component.scss']
})
export class PredictLandComponent {
  year!: number;
  stateEncoded!: number;
  landUseEncoded!: number;
  predictedUSD: number | null = null;
  predictedTND: number | null = null;
  predicting = false;

  private inputDim = 25;

  constructor(
    private predictionService: PredictionService,
    private currencyService:   CurrencyService
  ) {}

  onSubmit(form: NgForm) {
    if (form.invalid) return;

    const zeros   = Array(this.inputDim - 3).fill(0);
    const features = [
      this.year,
      this.stateEncoded,
      this.landUseEncoded,
      ...zeros
    ];

    this.predicting    = true;
    this.predictedUSD  = null;
    this.predictedTND  = null;

    // 1) get USD prediction
    this.predictionService.predict(features).subscribe({
      next: ({ predicted_price }) => {
        this.predictedUSD = predicted_price;
        // 2) fetch current USD→TND rate
        this.currencyService.getUsdToTndRate().subscribe({
          next: rate => {
            this.predictedTND = this.predictedUSD! * rate;
            this.predicting = false;
          },
          error: _ => {
            console.warn('Could not fetch TND rate');
            this.predicting = false;
          }
        });
      },
      error: err => {
        console.error('Prediction error', err);
        this.predicting = false;
      }
    });
  }
}
<div class="container p-4">
  <h2>Land Price Prediction</h2>
  <form #predictForm="ngForm" (ngSubmit)="onSubmit(predictForm)">
    <div class="mb-3">
      <label for="year" class="form-label">Year</label>
      <input
        type="number"
        id="year"
        name="year"
        class="form-control"
        required
        [(ngModel)]="year"
      >
    </div>
    <div class="mb-3">
      <label for="state" class="form-label">State (encoded)</label>
      <input
        type="number"
        id="state"
        name="state"
        class="form-control"
        required
        [(ngModel)]="stateEncoded"
      >
    </div>
    <div class="mb-3">
      <label for="landUse" class="form-label">Land Use (encoded)</label>
      <input
        type="number"
        id="landUse"
        name="landUse"
        class="form-control"
        required
        [(ngModel)]="landUseEncoded"
      >
    </div>
    <button type="submit"
            class="btn btn-primary"
            [disabled]="predicting">
      {{ predicting ? 'Predicting...' : 'Predict Price' }}
    </button>
  </form>

  <div *ngIf="predictedPrice !== null" class="alert alert-success mt-3">
    <strong>Predicted Price (USD/m²):</strong>
    {{ predictedPrice | number:'1.2-2' }}
  </div>
</div>
