import { Component, AfterViewInit, OnInit, EventEmitter, Output } from '@angular/core';
import { NgbDropdownModule, NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { AuthService } from 'src/app/Services/auth.service';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-navigation',
  standalone: true,
  imports: [NgbDropdownModule],
  templateUrl: './navigation.component.html'
})
export class NavigationComponent implements AfterViewInit, OnInit {
  @Output() toggleSidebar = new EventEmitter<void>();

  public showSearch = false;
  user: any;
  safeImageUrl: SafeUrl | string = 'assets/images/users/default.jpg'; // fallback image

  constructor(
    private modalService: NgbModal,
    private authService: AuthService,
    private sanitizer: DomSanitizer
  ) {}

  ngOnInit(): void {
    this.authService.getUserDetails().subscribe({
      next: (res) => {
        this.user = res;
        if (this.user?.imageUrl) {
          this.safeImageUrl = this.sanitizer.bypassSecurityTrustUrl(this.user.imageUrl);
        }
      },
      error: (err) => console.error('Failed to fetch user info:', err)
    });
  }

  ngAfterViewInit() {}
}
<ul class="navbar-nav float-end">
  <li class="nav-item" ngbDropdown placement="bottom-end">
    <a
      ngbDropdownToggle
      class="nav-link waves-effect waves-dark pro-pic d-flex align-items-center"
      href="javascript:void(0)"
      aria-haspopup="true"
      aria-expanded="false"
    >
      <img
        [src]="safeImageUrl"
        (error)="safeImageUrl = 'assets/images/users/default.jpg'"
        alt="user"
        class="rounded-circle border border-2 shadow-sm"
        width="36"
        height="36"
      />
      <span class="ms-2 fw-semibold">{{ user?.username || 'Guest' }}</span>
    </a>

    <div ngbDropdownMenu class="dropdown-menu dropdown-menu-end animate__animated animate__fadeIn">
      <!-- User preview in dropdown -->
      <div class="dropdown-header text-center">
        <img
          [src]="safeImageUrl"
          (error)="safeImageUrl = 'assets/images/users/default.jpg'"
          alt="user"
          class="rounded-circle mb-2 border"
          width="60"
          height="60"
        />
        <div class="fw-bold">{{ user.name || 'Guest User' }}</div>
        <small class="text-muted">{{ user?.email || '' }}</small>
      </div>
      <div class="dropdown-divider"></div>

      <a class="dropdown-item" href="/profile">üë§ My Profile</a>
