<div class="container p-4">
  <h2>Land Price Prediction</h2>

  <form #predictForm="ngForm" (ngSubmit)="onSubmit(predictForm)">
    <!-- your three inputs… -->
    <button type="submit"
            class="btn btn-primary"
            [disabled]="predictForm.invalid || predicting">
      {{ predicting ? 'Predicting…' : 'Predict Price' }}
    </button>
  </form>

  <!-- only show once we have a USD value -->
  <div *ngIf="predictedUSD !== null" class="alert alert-success mt-4">
    <p><strong>Price (USD/m²):</strong> {{ predictedUSD | number:'1.2-2' }}</p>

    <!-- always render this line: loader while fetching rate, then TND -->
    <p>
      <strong>Price (TND/m²):</strong>
      <ng-container *ngIf="loadingRate; else showTND">
        <em>Converting…</em>
      </ng-container>
      <ng-template #showTND>
        {{ predictedTND | number:'1.2-2' }}
      </ng-template>
    </p>
  </div>

  <!-- error if nothing returned -->
  <div *ngIf="!predicting && predictedUSD===null && predictForm.submitted"
       class="alert alert-danger mt-4">
    Prediction failed. Please try again.
  </div>
</div>
