// src/app/services/currency.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, map } from 'rxjs';

interface ExchangeResponse {
  rates: { TND: number };
}

@Injectable({ providedIn: 'root' })
export class CurrencyService {
  // Using exchangerate.host (no API key required)
  private apiUrl = 'https://api.exchangerate.host/latest?base=USD&symbols=TND';

  constructor(private http: HttpClient) {}

  getUsdToTndRate(): Observable<number> {
    return this.http.get<ExchangeResponse>(this.apiUrl)
                    .pipe(map(res => res.rates.TND));
  }
}
