// src/app/components/classifier/classifier.component.ts
import { Component } from '@angular/core';
import { ClassificationService, PredictionResponse } from '../../services/classification.service';

@Component({
  selector: 'app-classifier',
  templateUrl: './classifier.component.html',
  styleUrls: ['./classifier.component.css']
})
export class ClassifierComponent {
  lat!: number;
  lon!: number;
  prediction: string | null = null;
  loading = false;
  error: string | null = null;

  constructor(private classifier: ClassificationService) {}

  onSubmit() {
    this.prediction = null;
    this.error = null;
    this.loading = true;

    this.classifier.predict(this.lat, this.lon).subscribe({
      next: (res: PredictionResponse) => {
        this.prediction = res.prediction;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error fetching prediction';
        console.error(err);
        this.loading = false;
      }
    });
  }
}
