// src/app/components/classifier/classifier.component.ts
import { Component, AfterViewInit, OnInit } from '@angular/core';
import * as L from 'leaflet';
import { ClassificationService, PredictionResponse } from 'src/app/services/classification.service';

@Component({
  selector: 'app-classifier',
  templateUrl: './classifier.component.html',
  styleUrls: ['./classifier.component.scss']
})
export class ClassifierComponent implements OnInit, AfterViewInit {
  lat!: number;
  lon!: number;

  tileUrl: string | null = null;
  prediction: string | null = null;
  loading = false;
  error: string | null = null;

  private map!: L.Map;

  constructor(private svc: ClassificationService) {}

  ngOnInit(): void {
    this.map = L.map('map', {
      maxBounds: L.latLngBounds(
        L.latLng(-90, -180),
        L.latLng(90, 180)
      ),
      maxBoundsViscosity: 1.0
    }).setView([36.8065, 10.1815], 13);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors'
    }).addTo(this.map);

    this.map.on('click', (e: any) => {
      this.lat = e.latlng.lat;
      this.lon = e.latlng.lng;
    });
  }

  ngAfterViewInit(): void {
    const button = document.querySelector('.classifier-form button') as HTMLElement;
    if (button) {
      L.DomEvent.disableClickPropagation(button);
    }
  }

  onSubmit(): void {
    this.reset();
    this.loading = true;

    this.svc.predict(this.lat, this.lon).subscribe({
      next: (res: PredictionResponse & { tile: string }) => {
        this.prediction = res.prediction;
        this.tileUrl = `data:image/png;base64,${res.tile}`;
        this.loading = false;
      },
      error: () => {
        this.error = 'Failed to load tile or prediction';
        this.loading = false;
      }
    });
  }

  private reset(): void {
    this.tileUrl = null;
    this.prediction = null;
    this.error = null;
  }
}
