<div class="classifier-map" style="position: relative; margin: 0 auto; max-width: 600px;">
  <div id="map" style="height: 300px; width: 100%; border: 2px solid #ccc; border-radius: 8px; z-index: 0;"></div> <!-- Adjusted map dimensions and centered -->
</div>

<div class="classifier-form mt-4 text-center" style="z-index: 1; position: relative; max-width: 600px; margin: 0 auto;"> <!-- Centered button and form -->
  <button class="btn btn-success btn-lg" (click)="onSubmit()" [disabled]="loading || lat == null || lon == null">
    {{ loading ? 'Classifying…' : 'Classify Land' }}
  </button>
</div>

<div *ngIf="prediction" class="result mt-4 p-3" style="background-color: #e9f7ef; border: 1px solid #28a745; border-radius: 8px; max-width: 600px; margin: 0 auto;">
  <h3 class="text-success">Prediction:</h3>
  <p>{{ prediction }}</p>
  <div *ngIf="tileUrl" class="tile-container">
  <img [src]="tileUrl" alt="Sentinel tile" style="max-width:100%; border:1px solid #ccc;"/>
</div>

<div *ngIf="prediction" class="alert alert-success">
  <strong>Prediction:</strong> {{ prediction }}
</div>

</div>

<div *ngIf="error" class="error mt-4 p-3" style="background-color: #f8d7da; border: 1px solid #f5c6cb; border-radius: 8px; max-width: 600px; margin: 0 auto;">
  <h3 class="text-danger">Error:</h3>
  <p>{{ error }}</p>
</div>
