import { Component } from '@angular/core';
import { WeatherService } from 'src/app/Services/weather.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html',
  styleUrls: ['./weather.component.scss'],
})
export class WeatherComponent {
  lat: number = 0;
  lon: number = 0;
  weatherData: any = null;

  constructor(private weatherService: WeatherService) {}

  fetchWeather() {
    this.weatherService.getWeather(this.lat, this.lon).subscribe({
      next: (data) => this.weatherData = data,
      error: (err) => console.error('Failed to fetch weather:', err)
    });
  }
}
<div>
  <input type="number" [(ngModel)]="lat" placeholder="Latitude" />
  <input type="number" [(ngModel)]="lon" placeholder="Longitude" />
  <button (click)="fetchWeather()">Get Weather</button>
</div>

