import { Component } from '@angular/core';
import { Features } from 'src/app/models/features/features.model';
import { recommendationService } from 'src/app/Services/feature.service';


@Component({
  selector: 'app-feature',
  templateUrl: './feature.component.html',
  styleUrls: ['./feature.component.scss']
})
export class FeatureComponent {
  lat!: number;
  lon!: number;
  loading = false;
  error: string | null = null;
  features: Features | null = null;

  constructor(private svc: recommendationService) {}

// feature.component.ts
fetchFeatures() {
  this.loading = true;
  this.featureService.getFeatures(this.lat, this.lon)
    .subscribe({
      next: f => { this.features = f; this.loading = false; },
      error: e => { this.error = e.message; this.loading = false; }
    });
}


  objectKeys = Object.keys;

  getFeatureValue(key: string): any {
    return this.features ? this.features[key as keyof Features] : null;
  }
}
