import { Component, OnInit } from '@angular/core';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss'],
  standalone: false,
})
export class ProfileComponent implements OnInit {
  user: any;
  safeImageUrl: SafeUrl | undefined;

  constructor(
    private authService: AuthService,
    private sanitizer: DomSanitizer
  ) {}

  ngOnInit(): void {
    this.authService.getUserDetails().subscribe({
      next: (res) => {
        this.user = res;
        console.log('User:', this.user); // 🔍 Check if roles are present

        if (this.user?.imageUrl) {
          this.safeImageUrl = this.sanitizer.bypassSecurityTrustUrl(this.user.imageUrl);
        }
      },
      error: (err) => {
        console.error('Error fetching user details:', err);
      }
    });
  }

  getUserRoles(): string {
    if (!this.user?.roles || !Array.isArray(this.user.roles)) return 'No roles';
    return this.user.roles.map((r: any) => r.name).join(', ');
  }
}
<div class="profile-wrapper d-flex align-items-center justify-content-center" style="background: linear-gradient(to bottom, #6a11cb, #2575fc); height: 100vh;">
    <div class="card p-4 shadow-lg" style="width: 400px; background-color: rgba(255, 255, 255, 0.95); border-radius: 15px;">
        <h4 class="mb-3 text-center text-primary">User Profile</h4>

        <div class="text-center mb-3">
            <img *ngIf="safeImageUrl" [src]="safeImageUrl" alt="Profile Picture" class="rounded-circle border border-primary" style="width: 100px; height: 100px; object-fit: cover;">
            <div *ngIf="!safeImageUrl" class="placeholder rounded-circle border border-secondary" style="width: 100px; height: 100px; background-color: #f0f0f0;"></div>
        </div>

        <div class="mb-3">
            <label class="form-label fw-bold">Name:</label>
            <p class="form-control-static">{{ user?.firstName }} {{ user?.lastName }}</p>
        </div>

        <div class="mb-3">
            <label class="form-label fw-bold">Username:</label>
            <p class="form-control-static">{{ user?.username }}</p>
        </div>

        <div class="mb-3">
            <label class="form-label fw-bold">Roles:</label>
            <p class="form-control-static">{{ getUserRoles() }}</p>
        </div>

        <div class="text-center">
            <button class="btn btn-primary btn-lg" routerLink="/dashboard">Go to Dashboard</button>
        </div>
    </div>
</div>
