import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CropResponse } from 'src/app/Services/feature.service';

@Component({
  selector: 'app-feature',
  templateUrl: './feature.component.html',
  styleUrls: ['./feature.component.scss']
})
export class FeatureComponent implements OnInit {
  form!: FormGroup;
  result: string | null = null;
  loading = false;
  error: string | null = null;

  constructor(
    private fb: FormBuilder,
    private svc: FeatureService
  ) {}

  ngOnInit(): void {
    // build a form with all expected features
    this.form = this.fb.group({
      K:        [50,      [Validators.required, Validators.min(0)]],
      N:        [0.3,     [Validators.required, Validators.min(0)]],
      P:        [35,      [Validators.required, Validators.min(0)]],
      humidity: [78.5,    [Validators.required, Validators.min(0), Validators.max(100)]],
      ph:       [6.8,     [Validators.required, Validators.min(0), Validators.max(14)]],
      rainfall: [120,     [Validators.required, Validators.min(0)]],
      temperature: [22.5, [Validators.required]]
    });
  }

  onSubmit(): void {
    if (this.form.invalid) return;
    this.loading = true;
    this.result = this.error = null;

    this.svc.recommend(this.form.value).subscribe({
      next: (res: CropResponse) => {
        this.result = res.recommended_crop;
        this.loading = false;
      },
      error: (err) => {
        this.error = err.error?.detail || 'Server error';
        this.loading = false;
      }
    });
  }
}
