import { Component } from '@angular/core';
import { WeatherService } from '../../Services/weather.service';
import { SoilService } from '../../Services/soil.service';

@Component({
  selector: 'app-weather',
  templateUrl: './weather.component.html'
})
export class WeatherComponent {
  lat: number = 0;
  lon: number = 0;

  weatherData: any = null;

  soilData: {
    ph: number;
    nitrogen: number;
    phosphorus: number;
    potassium: number;
  } | null = null;

  constructor(
    private weatherService: WeatherService,
    private soilService: SoilService
  ) {}

  fetchData() {
    // Get weather
    this.weatherService.getWeather(this.lat, this.lon).subscribe({
      next: (data) => this.weatherData = data,
      error: (err) => console.error('Weather error:', err)
    });

    // Get soil
    this.soilService.getSoil(this.lat, this.lon).subscribe({
      next: (data) => {
        this.soilData = {
          ph: data.ph ?? -1,
          nitrogen: data.nitrogen ?? -1,
          phosphorus: data.phosphorus ?? -1,
          potassium: data.potassium ?? -1
        };
      },
      error: (err) => console.error('Soil error:', err)
    });
  }
}
<div>
  <input [(ngModel)]="lat" type="number" placeholder="Latitude" />
  <input [(ngModel)]="lon" type="number" placeholder="Longitude" />
  <button (click)="fetchData()">Get Weather & Soil</button>
</div>

<div *ngIf="weatherData">
  <h3>ðŸŒ¦ Weather</h3>
  <p>Temperature: {{ weatherData.temperature }} Â°C</p>
  <p>Humidity: {{ weatherData.humidity }} %</p>
  <p>Rainfall: {{ weatherData.rainfall }} mm</p>
</div>
<div *ngIf="soilData">
  <h3>Soil</h3>
  <p *ngIf="soilData.ph >= 0">pH: {{ soilData.ph }}</p>
  <p *ngIf="soilData.nitrogen >= 0">Nitrogen (N): {{ soilData.nitrogen }} %</p>
  <p *ngIf="soilData.phosphorus >= 0">Phosphorus (P): {{ soilData.phosphorus }} mg/kg</p>
  <p *ngIf="soilData.potassium >= 0">Potassium (K): {{ soilData.potassium }} mg/kg</p>
  <p *ngIf="soilData.ph < 0 && soilData.nitrogen < 0">No soil data available.</p>
</div>
