// src/app/components/classifier/classifier.component.ts
import { Component, OnInit, AfterViewInit } from '@angular/core';
import { ClassificationService, PredictionResponse } from 'src/app/Services/classification.service';
import { NgIf } from '@angular/common';
import { FormsModule } from '@angular/forms';
import * as L from 'leaflet';

@Component({
  selector: 'app-classifier',
  templateUrl: './classifier.component.html',
  styleUrls: ['./classifier.component.scss']
})
export class ClassifierComponent implements OnInit, AfterViewInit {
  lat!: number;
  lon!: number;
  prediction: string | null = null;
  loading = false;
  error: string | null = null;

  constructor(private classifier: ClassificationService) {}

  ngOnInit(): void {
    const map = L.map('map').setView([36.8065, 10.1815], 13); // Default view

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    map.on('click', (e: any) => {
      const { lat, lng } = e.latlng;
      this.lat = lat;
      this.lon = lng;
      console.log(`Selected location: Latitude ${lat}, Longitude ${lng}`);
    });
  }

  ngAfterViewInit(): void {
    // Prevent map from intercepting button clicks
    const button = document.querySelector('.classifier-form button') as HTMLElement;
    if (button) {
      L.DomEvent.disableClickPropagation(button);
    }
  }

  onSubmit() {
    this.prediction = null;
    this.error = null;
    this.loading = true;

    this.classifier.predict(this.lat, this.lon).subscribe({
      next: (res: PredictionResponse) => {
        this.prediction = res.prediction;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Error fetching prediction';
        console.error(err);
        this.loading = false;
      }
    });
  }
}
<!-- src/app/components/classifier/classifier.component.html -->
<div class="classifier-map">
  <div id="map" style="height: 300px; width: 100%;"></div> <!-- Reduced map height -->
</div>

<div class="classifier-form mt-3"> <!-- Added margin-top for spacing -->
  <button class="btn btn-primary" (click)="onSubmit()" [disabled]="loading || lat == null || lon == null">
    {{ loading ? 'Classifying…' : 'Classify Land' }}
  </button>
</div>

<div *ngIf="prediction" class="result mt-3">
  <h3>Prediction:</h3>
  <p>{{ prediction }}</p>
</div>

<div *ngIf="error" class="error mt-3">
  {{ error }}
</div>
