import { Component, OnInit } from '@angular/core';
import { DomSanitizer, SafeUrl } from '@angular/platform-browser';
import { AuthService } from 'src/app/Services/auth.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.scss'],
  standalone: false,
})
export class ProfileComponent implements OnInit {
  user: any;
  safeImageUrl: SafeUrl | undefined;

  constructor(
    private authService: AuthService,
    private sanitizer: DomSanitizer
  ) {}

  ngOnInit(): void {
    this.authService.getUserDetails().subscribe({
      next: (res) => {
        this.user = res;
        console.log('User:', this.user); // ðŸ” Check if roles are present

        if (this.user?.imageUrl) {
          this.safeImageUrl = this.sanitizer.bypassSecurityTrustUrl(this.user.imageUrl);
        }
      },
      error: (err) => {
        console.error('Error fetching user details:', err);
      }
    });
  }

  getUserRoles(): string {
    if (!this.user?.roles || !Array.isArray(this.user.roles)) return 'No roles';
    return this.user.roles.map((r: any) => r.name).join(', ');
  }
}
  <div class="text-center mb-4">
      <img src="assets/images/logos/farmwiselogo.png" alt="Farmwise Logo" style="width: 150px;">
    </div>
<div class="profile-wrapper d-flex align-items-center justify-content-center">
  <div class="card p-4" style="width: 400px;">
    <h4 class="mb-3 text-center">User Profile</h4>

    <div class="text-center mb-3">
      <img *ngIf="safeImageUrl" [src]="safeImageUrl" alt="Profile Picture" class="rounded-circle" style="width: 100px; height: 100px;">
      <div *ngIf="!safeImageUrl" class="placeholder rounded-circle" style="width: 100px; height: 100px; background-color: #f0f0f0;"></div>
    </div>

    <div class="mb-3">
      <label class="form-label">Name:</label>
      <p class="form-control-static">{{ user?.firstName }} {{ user?.lastName }}</p>
    </div>

    <div class="mb-3">
      <label class="form-label">Username:</label>
      <p class="form-control-static">{{ user?.username }}</p>
    </div>


    <div class="mb-3">
      <label class="form-label">Roles:</label>
      <p class="form-control-static">{{ getUserRoles() }}</p>
    </div>

    <div class="text-center">
      <button class="btn btn-primary" routerLink="/dashboard">Go to Dashboard</button>
    </div>
  </div>
</div>
